using System;using System.Web.Mvc;using Serilog;namespace BookStore.ErrorHandler{    [AttributeUsage(AttributeTargets.Class | AttributeTargets.Method, Inherited = true, AllowMultiple = true)]     public class ErrorHandlerAttribute : HandleErrorAttribute    {        public override void OnException(ExceptionContext filterContext)        {            if (filterContext != null && filterContext.HttpContext != null && filterContext.Exception != null)            {                Log.Logger.Error(filterContext.Exception, "Critical error occured");            }            base.OnException(filterContext);        }    }}